import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-professors',
  templateUrl: './professors.component.html',
  styleUrls: ['./professors.component.css']
})
export class ProfessorsComponent implements OnInit {

  // Sample data (you can replace this later with data from your database)
  professors = [
    {
      name: 'Dr. John Doe',
      rating: 4,
      courses: ['Calculus I', 'Calculus II', 'Linear Algebra']
    },
    {
      name: 'Dr. Jane Smith',
      rating: 5,
      courses: ['Introduction to Programming', 'Data Structures', 'Software Engineering']
    },
    {
      name: 'Dr. Robert Brown',
      rating: 3,
      courses: ['Physics I', 'Quantum Mechanics']
    }
  ];

  constructor() { }

  ngOnInit(): void {
  }

}
<div class="professor-container" *ngFor="let professor of professor">
    <h3>{{ professor.name }}</h3>
    <div class="rating">
        <!-- Display star ratings based on professor's numeric rating -->
        <ng-container *ngFor="let r of [1,2,3,4,5]">
            <i class="fa" [ngClass]="r <= professor.rating ? 'fa-star' : 'fa-star-o'"></i>
        </ng-container>
        <span>({{ professor.rating }}/5)</span>
    </div>
    <div class="courses">
        <h4>Courses Taught:</h4>
        <ul>
            <li *ngFor="let course of professor.courses">{{ course }}</li>
        </ul>
    </div>
</div>
